import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import axios from "axios";

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [stats, setStats] = useState({
    customers: 0,
    products: 0,
    orders: 0,
  });

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (token) {
      // Decode token to get user info (simplest way)
      const payload = JSON.parse(atob(token.split(".")[1]));
      setUser({ id: payload.userId });
    }

    // Fetch dashboard stats from backend
    const fetchStats = async () => {
      try {
        const res = await axios.get("http://localhost:5002/api/dashboard/stats", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setStats(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchStats();
  }, [token]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.href = "/login";
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar user={user} onLogout={handleLogout} />
      <div className="p-6 grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-white shadow rounded p-4">
          <h2 className="font-bold text-lg">Customers</h2>
          <p className="text-3xl">{stats.customers}</p>
        </div>
        <div className="bg-white shadow rounded p-4">
          <h2 className="font-bold text-lg">Products</h2>
          <p className="text-3xl">{stats.products}</p>
        </div>
        <div className="bg-white shadow rounded p-4">
          <h2 className="font-bold text-lg">Orders</h2>
          <p className="text-3xl">{stats.orders}</p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
